        g_form.addOption('e_voucher_brand', 'Myntra');
        g_form.removeOption('e_voucher_amount', 100);
        g_form.removeOption('e_voucher_amount', 400);
        g_form.removeOption('e_voucher_amount', 750);
        g_form.addOption('e_voucher_amount', 200);
        g_form.addOption('e_voucher_amount', 250);
        g_form.addOption('e_voucher_amount', 500);
        g_form.addOption('e_voucher_amount', 1000);

    } else if (newValue == 'Shopping') {
        g_form.removeOption('e_voucher_brand', 'Flipkart');
