        g_form.addOption('e_voucher_brand', 'Adidas');
        g_form.addOption('e_voucher_brand', 'Nike');
        g_form.addOption('e_voucher_brand', 'Puma');
        g_form.removeOption('e_voucher_amount', 100);
        g_form.removeOption('e_voucher_amount', 200);
        g_form.removeOption('e_voucher_amount', 400);
        g_form.addOption('e_voucher_amount', 750);
