catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    if (newValue == 'E-Commerce') {

        g_form.clearOptions('e_voucher_brand');
        g_form.addOption('e_voucher_brand', 'Flipkart','Flipkart','1');
        g_form.addOption('e_voucher_brand', 'Amazon','Amazon','2');
        g_form.addOption('e_voucher_brand', 'Myntra','Myntra','3');
        g_form.clearOptions('e_voucher_amount');
        g_form.addOption('e_voucher_amount', '200','Rs.200','1');
        g_form.addOption('e_voucher_amount', '250','Rs.250','2');
        g_form.addOption('e_voucher_amount', '500','Rs.500','3');
        g_form.addOption('e_voucher_amount', '1000','Rs.1000','4');

    } else if (newValue == 'Shopping') {
        g_form.clearOptions('e_voucher_brand');
        g_form.addOption('e_voucher_brand', 'Lifestyle','Lifestyle','1');
        g_form.addOption('e_voucher_brand', 'Allen Solly','Allen Solly','2');
        g_form.clearOptions('e_voucher_amount');
        g_form.addOption('e_voucher_amount', '750','Rs.750','1');
        g_form.addOption('e_voucher_amount', '400','Rs.400','2');
        g_form.addOption('e_voucher_amount', '500','Rs.500','3');
        g_form.addOption('e_voucher_amount', '1000','Rs.1000','4');
    } else if (newValue == 'Sports Wear') {
        g_form.clearOptions('e_voucher_brand');
        g_form.addOption('e_voucher_brand', 'Adidas','Adidas','1');
        g_form.addOption('e_voucher_brand', 'Nike','Nike','2');
        g_form.addOption('e_voucher_brand', 'Puma','Puma','3');
        g_form.clearOptions('e_voucher_amount');
        g_form.addOption('e_voucher_amount', '750','Rs.750','1');
        g_form.addOption('e_voucher_amount', '250','Rs.250','2');
        g_form.addOption('e_voucher_amount', '500','Rs.500','3');
        g_form.addOption('e_voucher_amount', '1000','Rs.1000','4');
    } else {
        g_form.clearOptions('e_voucher_brand');
        g_form.addOption('e_voucher_brand', 'Zomato','Zomato','1');
        g_form.addOption('e_voucher_brand', 'Swiggy','Swiggy','2');
        g_form.clearOptions('e_voucher_amount');
        g_form.addOption('e_voucher_amount', '100','Rs.100','1');
        g_form.addOption('e_voucher_amount', '200','Rs.200','2');
        g_form.addOption('e_voucher_amount', '400','Rs.400','3');
        g_form.addOption('e_voucher_amount', '1000','Rs.1000','4');

    }


    //Type appropriate comment here, and begin script below

}
